using FSH.WebApi.Infrastructure.Common.Utils;
using Xunit;

namespace Infrastructure.Test.Common.Utils;
public class EncryptionUtilsTest
{
    [Fact]
    public void SimpleEncDecryptReturnsOriginalString()
    {
        // Arrange
        string originalText = "Hello, World!";

        // Act
        string encryptedText = EncryptionUtils.SimpleEnc(originalText);
        string decryptedText = EncryptionUtils.SimpleDec(encryptedText);

        // Assert
        Assert.Equal(originalText, decryptedText);
    }

    [Fact]
    public void SimpleEncProducesDifferentOutputEachTime()
    {
        // Arrange
        string originalText = "Hello, World!";

        // Act
        string encryptedText1 = EncryptionUtils.SimpleEnc(originalText);
        string encryptedText2 = EncryptionUtils.SimpleEnc(originalText);

        // Assert
        Assert.NotEqual(encryptedText1, encryptedText2);
    }

    [Fact]
    public void SimpleDecThrowsExceptionOnInvalidInput()
    {
        // Arrange
        string invalidEncryptedText = "InvalidBase64String";

        // Act & Assert
        _ = Assert.Throws<FormatException>(() => EncryptionUtils.SimpleDec(invalidEncryptedText));
    }

    [Fact]
    public void SimpleEncDecryptLongString()
    {
        // Arrange
        string originalText = new('A', 1000); // Long string

        // Act
        string encryptedText = EncryptionUtils.SimpleEnc(originalText);
        string decryptedText = EncryptionUtils.SimpleDec(encryptedText);

        // Assert
        Assert.Equal(originalText, decryptedText);
    }

    [Fact]
    public void SimpleEncDecryptEmptyString()
    {
        // Arrange
        string originalText = string.Empty; // Empty string

        // Act
        string encryptedText = EncryptionUtils.SimpleEnc(originalText);
        string decryptedText = EncryptionUtils.SimpleDec(encryptedText);

        // Assert
        Assert.Equal(originalText, decryptedText);
    }

    // Test Decrypt method
    [Fact]
    public void DecryptWhenCalledWithValidParametersShouldReturnDecryptedString()
    {
        // Arrange
        const string input = "";

        // Act
        string decrypted = EncryptionUtils.SimpleDec(input);

        // Assert
        Assert.NotEmpty(decrypted);
    }
}
